import { Plugin } from 'vite';

interface MdH1Option {
    /**
     * 忽略的文件路径，支持正则表达式
     *
     * @default []
     */
    ignoreList?: Array<RegExp | string>;
    /**
     * 添加一级标题前的钩子，如果返回 false，则不添加一级标题，如果返回 string，则使用返回值作为一级标题，string 必须是一个非空字符串
     */
    beforeInject?: (frontmatter: Record<string, any>, id: string, title: string) => boolean | string | undefined;
}

declare function VitePluginVitePressMdH1(option?: MdH1Option): Plugin & {
    name: string;
};
/**
 * 获取实际的文件名
 *
 * @param filename 文件名
 */
declare const getMdFileTitle: (filename: string) => string;

export { type MdH1Option, VitePluginVitePressMdH1 as default, getMdFileTitle };
