interface PermalinkOption {
    /**
     * 忽略的文件/文件夹列表，支持正则表达式
     *
     * @default []
     */
    ignoreList?: Array<RegExp | string>;
    /**
     * 文章所在的目录，基于 .vitepress 目录层级添加，开头不需要有 /
     * @default 'vitepress 的 srcDir 配置项'
     */
    path?: string;
}
interface Permalink {
    /**
     * key 为文件相对路径，value 为永久链接
     */
    map: Record<string, string>;
    /**
     * key 为永久链接，value 为文件相对路径
     */
    inv: Record<string, string>;
}

/**
 * 创建 rewrites，使用该函数创建的 rewrites 后，将不再加载 vitepress-plugin-permalink 插件拦截逻辑
 */
declare const createRewrites: (options?: PermalinkOption & {
    srcDir?: string;
    locales?: Record<string, any>;
}) => Record<string, string>;
/**
 * 给 permalink 添加多语言前缀
 *
 * @param localesKeys 多语言 key 数组，排除 root 根目录
 * @param path 文件路径
 * @param permalink 永久链接
 */
declare const getLocalePermalink: (localesKeys?: string[], path?: string, permalink?: string) => string;

export { type PermalinkOption as P, type Permalink as a, createRewrites as c, getLocalePermalink as g };
