import { MaybeRefOrGetter, Ref } from 'vue';
export interface UseSwitchDataOption {
    /**
     * 切换间隔时间，单位：毫秒
     */
    timeout?: number;
    /**
     * 是否随机切换数据
     */
    shuffle?: boolean;
    /**
     * data 发生变化，是否重新加载
     *
     * @default false
     */
    reloadWhenDataChanged?: boolean;
    /**
     * 切换数据之前执行的回调函数
     */
    onBeforeUpdate?: (newValue: string) => void;
    /**
     * 自定义切换逻辑
     */
    onUpdate?: (data: Ref<string>, newValue: string) => void;
    /**
     * 切换数据之后执行的回调函数
     */
    onAfterUpdate?: (newValue: string) => void;
}
/**
 * 从数据列表里按顺序/随机获取一笔数据
 *
 * @param dataList 数据列表
 * @param options 选项
 */
export declare const useSwitchData: (dataList: MaybeRefOrGetter<any[]>, options?: UseSwitchDataOption) => {
    data: Ref<any, any>;
    index: Ref<number, number>;
    start: () => void;
    stop: (restore?: boolean) => void;
    restart: () => void;
};
export type UseSwitchDataReturn = ReturnType<typeof useSwitchData>;
